console.log("==============String DataType==============");

var firstName = "Sakshi";
var result = typeof firstName;
console.log("First Name :", firstName, "    ,", "Type:",result);

var lastName = "garudi";
var result = typeof lastName;
console.log("Last Name  :", lastName, "    ,", "Type:",result);

var city = "pune";
var result = typeof city;
console.log("City       :", city,  "      ,", "Type:", result);


console.log("==============Number DataType==============");

var mobileNo = 1234567890;
var result = typeof mobileNo;
console.log("Mobile No. :", mobileNo, ",", "Type:", result);

var pinCode = 411057;
var result = typeof pinCode;
console.log("Pincode    :", pinCode, "    ,", "Type:", result);

var clgPin = 411033;
var result = typeof clgPin;
console.log("Clg Pincode:", clgPin, "    ,", "Type:", result);

console.log("==============Boolean DataType==============");

var isMarried = false;
var result = typeof isMarried;
console.log("Married    :", isMarried, "     ,", "Type:", result);

var isPass = true;
var result = typeof isPass;
console.log("Pass       :", isPass, "      ,", "Type:", result);

var isWorking = false;
var result = typeof isWorking;
console.log("Working    :", isWorking, "     ,", "Type;", result);













