console.log(`============== Table of 10 in reverse order ============== `);
num = " ";
var i = 5;
while (i <= 15) {
  console.log(i);
  num += i + " ";
  i++;
  
}
console.log(`-------- In Horizontal Line --------`);
console.log(num.trim());

console.log(`============== Number from 50 to 40 ============== `);
num = "";
var i = 50;
while (i >= 40) {
  console.log(i);
  num += i +" ";
  i--;
 
}
console.log(`-------- In Horizontal Line --------`);
console.log(num.trim());

console.log(`============== First 15 odd number ============== `);
num = "";
count = 0;
var i = 1;
while (count <= 15) {
  console.log(i);
  count = count +1;
  num += i +" ";
  i= i+2;
 
}
console.log(`-------- In Horizontal Line --------`);
console.log(num.trim());

