console.log(`============== Table of 10 in reverse ============== `);
num = "";
var i = 100;
while (i >= 10) {
  console.log(i);
  num += i +" ";
  i= i-10;
 
}
console.log(`-------- In Horizontal Line --------`);
console.log(num.trim());